import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { deleteHTTPHistory } from '../store/actions/httpHistoryActions'
import moment from 'moment';


class HistorySummary extends Component {
    state = {}
    handleClick = (e) => {
        e.preventDefault()
        this.props.deleteHTTPHistory(this.props.historyData);
    }
    render() {
        console.log('this is history', this.props.historyData)

        if (this.props.historyData.userId === this.props.auth.uid) {
            return (<React-fragment>
                {this.props.historyData.userId === this.props.auth.uid && <div className='container' style={{ overflow: 'auto' }}>
                    <Link to={'/'}>
                        <div style={{ color: 'green', textDecoration: 'none', fontWeight: 'bold' }}>{this.props.historyData.method}</div>
                    </Link>
                    <span>{this.props.historyData.url} </span>
                    <button style={{ color: 'red' }} onClick={() => this.handleClick(this)} >X</button>
                    <p>{moment(this.props.historyData.createdAt.toDate()).fromNow()}</p>
                </div>}

            </React-fragment>);
        }
        else {
            return (
                <div className="container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column' }}>
                    <h5>No history Add some new items</h5>
                </div>
            );
        }

    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        deleteHTTPHistory: (history) => dispatch(deleteHTTPHistory(history))
    };
};


export default connect(null, mapDispatchToProps)(HistorySummary)
