import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import React, { Component } from 'react';
import HttpRequest from './HttpRequestHeaders'
import uuid from 'uuid';
class Newtabs extends Component {
  state = {
    arr: [{ id: 1, name: 'tab 1', content: <HttpRequest/> }]
  };

  handleClick = () => {
    const { arr } = this.state;
    const newTabObject = {
      id: uuid(),
      name: `tab ${arr.length + 1}`,
      content: <HttpRequest/>
    };
    this.setState({
      arr: [...arr, newTabObject]
    });
  };

  handleDelete = (toDelete) => {
    // console.log(toDelete)
    const { arr } = this.state;
    const tabDeleteIndex = arr.findIndex((arr) => arr.id === toDelete);
    const updatedTabs = arr.filter((tab, index) => {
      return index !== tabDeleteIndex;
    });
    // const previousTab=arr[tabDeleteIndex-1] || arr[tabDeleteIndex+1] ||{};
    this.setState({
      arr: updatedTabs
    });
  };

  render() {
    const { arr } = this.state;
    // console.log(arr)
    // console.log(this.state.arr)
    const name = arr.map((data, index) => {
      return (
        <Tab>
          {data.name}{' '}
          <button onClick={() => this.handleDelete(data.id)} className="btn">
            X
          </button>
        </Tab>
      );
    });
    const content = this.state.arr.map((data) => {
      return (
        <TabPanel>
          <h2>{data.content}</h2>
        </TabPanel>
      );
    });

    const fun = (
      <Tabs>
        <TabList>
          {name}
          <button onClick={this.handleClick} className="btn">
            +
          </button>
        </TabList>

        {content}
      </Tabs>
    );

    return <div>{fun}</div>;
  }
}

export default Newtabs;
