import React, { Component } from 'react';
import '../App.css';
import HistorySummary from './historySummary'
// import undefined from 'firebase/empty-import';

class Sidebar extends Component {
  state = {
    showHistory: true,
    showCollection: false,
    hello: []
  };
  // componentDidMount() {
  //   if (this.props.history !== undefined) {
  //     Object.values(this.props.history).map(d => {
  //       this.setState({ hello: d })
  //     })

  //   }
  // }
  onClickHistory = () => {
    this.setState({
      showHistory: true,
      showCollection: false
    });
  };
  onClickCollection = () => {
    this.setState({
      showCollection: true,
      showHistory: false
    });
  };

  render() {


    return (
      <>
        <div className="historyAndCollection">
          <button onClick={this.onClickHistory}>history</button>
          <button oncClick={this.onClickCollection}>Collection</button>
        </div>
        <div>
          {this.state.showHistory ? <div>  {this.props.history &&
            this.props.history.map((historyData, index) => {
              return (
                <HistorySummary historyData={historyData} key={index} />
              );
            })}</div> : null}
          {this.state.showCollection ? <div>{}</div> : null}
        </div>
      </>
    );
  }
}

export default Sidebar;
