import React, { Component } from "react"
import { connect } from 'react-redux';
import { httpHistory } from '../store/actions/httpHistoryActions'
var JSONpretty = require('react-json-pretty');

// import firebase from "../config/fbConfig"
//console.log(firebase.firestore().collection("test"));

class HttpRequest extends Component {
  state = {
    url: `${this.props.historyData.url}`,
    result: [],
    method: "GET",
    body: "",
    headerKeys: ["Content-Type"],
    headerValues: ["application/json"],
    header: {},
    showHeader: false,
    showBody: true
  };

  // componentWillReceiveProps(){
  //   console.log("receive props called")
  //   if(this.props.history!=undefined){
  //     console.log('this is props', this.props.history,);
  //     this.setState({url: this.props.history[0].url,
  //       method: this.props.history[0].method,
  //       bodyKeys: this.props.history[0].bodyKeys,
  //       bodyValues: this.props.history[0].bodyValues,
  //       body: this.props.history[0].body,
  //       headerKeys: this.props.history[0].headerKeys,
  //       headerValues: this.props.history[0].headerValues,
  //       header: this.props.history[0].body
  //        })
  //   }
  //  }
  handleUrlChange = event => {
    this.setState({ url: event.target.value });
  };
  handleFormSubmit = event => {
    event.preventDefault();
    const headObj = {};
    this.state.headerKeys.map((headerKey, index) => {
      return (headObj[headerKey] = this.state.headerValues[index]);
    });
    let bodyInput = "";
    if (this.state.body.length != 0) {
      bodyInput = JSON.parse(this.state.body)
    }

    this.setState({ body: JSON.stringify(bodyInput), header: headObj }, () => {
      this.props.httpHistory(this.state, this.props.auth)
      if (this.state.method === "GET") {
        fetch(this.state.url, {
          headers: this.state.header
        })
          .then(a => a.json())
          .then(result => this.setState({ result: JSON.stringify(result) }))
          .catch(err => {
            //console.log("error" + err);
          });
        //console.log(this.state);
      } else {
        fetch(this.state.url, {
          headers: this.state.header,
          method: this.state.method,
          body: JSON.stringify(this.state.body)
        })
          .then(a => a.json())
          .then(result => this.setState({ result: JSON.stringify(result) }))
          .catch(err => {
            //console.log("error" + err);
          });
        //console.log(this.state);
      }

    })


  };

  handleSelectChange = event => {
    this.setState({ method: event.target.value });
  };

  handleHeaderRemove = index => {
    this.state.headerKeys.splice(index, 1);
    this.state.headerValues.splice(index, 1);
    this.setState({ headerKeys: this.state.headerKeys });
    this.setState({ headervalues: this.state.headerValues });
  };

  handleHeaderKeyChange = (e, index) => {
    this.state.headerKeys[index] = e.target.value;
    // //console.log(this.state.bodyKey);
    this.setState({ headerKeys: this.state.headerKeys });
  };

  handleHeaderValueChange = (e, index) => {
    this.state.headerValues[index] = e.target.value;
    // //console.log(this.state.bodyKey);
    this.setState({ headerValues: this.state.headerValues });
  };



  addHeader = () => {
    this.setState({ headerKeys: [...this.state.headerKeys, ""] });
    this.setState({ headerValues: [...this.state.headerValues, ""] });
  };
  onClickHeader = () => {
    this.setState({ showHeader: true, showBody: false });
  };
  onClickBody = () => {
    this.setState({ showHeader: false, showBody: true });
  };
  handleBodyRawInputChange = e => {
    this.setState({ body: e.target.value });
  };

  render() {
    const { historyData } = this.props;
    console.log('this is what i get', historyData.url)
    return (
      <div className="urlArea">
        <form onSubmit={this.handleFormSubmit}>
          <select onChange={this.handleSelectChange}>
            <option value="GET">GET</option>
            <option value="PUT">PUT</option>
            <option value="POST">POST</option>
            <option value="DELETE">DELETE</option>
          </select>
          <input
            type="text"
            value={this.state.url}
            onChange={this.handleUrlChange}
            className="inputUrl"
          />
          <input type="submit" value="Send" className="sendButton" />

        </form>
        <nav className="header-body-nav">
          <button className="header-body-tab" onClick={this.onClickHeader}>
            Header
          </button>
          <button className="header-body-tab" onClick={this.onClickBody}>
            Body
          </button>
        </nav>
        {this.state.showBody ? (
          <span>
            <textarea
              className="body-raw-input"
              placeholder={"Body"}
              value={this.state.body}
              onChange={e => {
                this.handleBodyRawInputChange(e);
              }}
            />
          </span>
        ) : null}
        {this.state.showHeader ? (
          <span>
            {this.state.headerKeys.map((headerKey, index) => {
              return (
                <div key={index}>
                  <input
                    placeholder={"Key"}
                    value={headerKey}
                    onChange={e => {
                      this.handleHeaderKeyChange(e, index);
                    }}
                  />
                  <input
                    placeholder={"value"}
                    value={this.state.headerValues[index]}
                    onChange={e => {
                      this.handleHeaderValueChange(e, index);
                    }}
                  />
                  <button onClick={() => this.handleHeaderRemove(index)}>
                    Remove
                  </button>
                </div>
              );
            })}
            <button onClick={e => this.addHeader(e)}>Add Header</button>

          </span>
        ) : null}
        <div style={{ height: 500, overflow: 'auto' }}>
          <span >{this.state.result.length === 0 ? null : <JSONpretty id="response-area" data={this.state.result} />}</span>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    historyData: state.historyData
  }
}


const mapDispatchToProps = (dispatch) => {
  return {
    httpHistory: (newHttpData, auth) => dispatch(httpHistory(newHttpData, auth))
  }
}
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HttpRequest);
