import React, { Component } from 'react';
import '../App.css';
// import undefined from 'firebase/empty-import';

class Sidebar extends Component {
  state = {
    showHistory: true,
    showCollection: false,
    hello: []
  };
  componentDidMount() {
    if (this.props.history !== undefined) {
      Object.values(this.props.history).map(d => {
        this.setState({ hello: d })
      })
      // console.log(history[0].method)
    }
  }
  onClickHistory = () => {
    this.setState({
      showHistory: true,
      showCollection: false
    });
  };
  onClickCollection = () => {
    this.setState({
      showCollection: true,
      showHistory: false
    });
  };

  render() {
    console.log(this.state.hello)
    console.log(this.props.history)



    return (
      <>
        <div className="historyAndCollection">
          <button onClick={this.onClickHistory}>history</button>
          <button oncClick={this.onClickCollection}>Collection</button>
        </div>
        <div>
          {this.state.showHistory ? <div>{'hello' && 'hello'}</div> : null}
          {this.state.showCollection ? <div>{}</div> : null}
        </div>
      </>
    );
  }
}

export default Sidebar;
