import React, { Component } from 'react';
import { connect } from 'react-redux';
import { httpHistory } from '../store/actions/httpHistoryActions';
import HttpRequestHeaders from './HttpRequestHeaders';
import HttpRequestBody from './HttpRequestBody';
import HttpResponse from './HttpResponse';
import { Tabs, Tab, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import { addCurrentTabId } from '../store/actions/tabHistoryActions';

class HttpRequest extends Component {
  constructor(props)
  {
    super(props)
  }
  state = {
    url: '',
    result: '',
    method: 'GET',
    body: '',
    headerKeys: ['Content-Type'],
    headerValues: ['application/json'],
    headers: {},
    test: '',
    testOutput: ''
  };
  componentWillReceiveProps() {
    this.props.addCurrentTabId(this.props.index);

    let history = this.props.historyData;
    console.log('insdide compoennent will recuevrf');
    if (Object.keys(history).length !== 0 && this.props.previousTab) {
      console.log(
        'current tab',
        this.props.previousTab,
        'current index',
        this.props.index
      );

      // if(this.props.previousTab===this.props.index)
      // {
      // this.setState({
      //   url: history.url,
      //   method: history.method,
      //   body: history.body,
      //   headerKeys: history.headerKeys,
      //   headerValues: history.headerValues,
      //   headers: history.headers
      // });
      // }
      // else
      // {
      // this.setState({
      //   url: "",
      //   method: 'GET',
      //   body: '',
      //   headerKeys: ['Content-Type'],
      //   headerValues: ['application/json'],
      //   headers: {}
      // });
      // }
    }
    // this.props.addCurrentTabId(this.props.index)
  }
  componentWillMount() {
    let history = this.props.historyData;
    if (Object.keys(history).length !== 0) {
      console.log('inside the component did mount');
      if (this.props.index === this.props.previousTab) {
        this.setState({
          url: history.url,
          method: history.method,
          body: history.body,
          headerKeys: history.headerKeys,
          headerValues: history.headerValues,
          headers: history.headers
        });
      } else {
        this.setState({
          url: '',
          method: 'GET',
          body: '',
          headerKeys: ['Content-Type'],
          headerValues: ['application/json'],
          headers: {}
        });
      }
    }
  }

  compareTestWithResponse = (result) => {
    if (this.state.test.length === 0) {
      return;
    }
    try {
      let test = JSON.parse(this.state.test);
      let jsonKeyinTest = Object.keys(test);
      let jsonKeyInResult = Object.keys(result);
      if (jsonKeyInResult.length !== jsonKeyinTest.length) {
        this.setState({ testOutput: 'Failed length not equal' });
        console.log('Failed length not equal');
        return;
      } else {
        for (let i = 0; i < jsonKeyInResult.length; i++) {
          if (!jsonKeyinTest.includes(jsonKeyInResult[i])) {
            this.setState({ testOutput: 'Failed Keys not present' });
            console.log(
              'failed keys not present' + jsonKeyinTest + ',' + jsonKeyInResult
            );
            return;
          }
          if (test[jsonKeyInResult[i]] !== result[jsonKeyInResult[i]]) {
            this.setState({
              testOutput:
                'Failed not equal properties of ' + jsonKeyInResult[i] + ''
            });
            console.log('failed not equal properties of' + jsonKeyInResult[i]);
            return;
          }
        }
        this.setState({ testOutput: 'Passed' });
        console.log('Passed');
      }
    } catch (err) {
      console.log('badly formatted test cases');
    }
  };
  handleUrlChange = (event) => {
    this.setState({ url: event.target.value });
  };
  handleFormSubmit = (event) => {
    event.preventDefault();
    if (this.state.url !== '') {
      const headObj = {};
      this.state.headerKeys.map((headerKey, index) => {
        return (headObj[headerKey] = this.state.headerValues[index]);
      });
      let bodyInput = '';

      this.setState({ headers: headObj }, () => {
        this.props.httpHistory(this.state, this.props.auth);
        console.log(this.state);
        if (this.state.method === 'GET') {
          fetch(this.state.url, {
            headers: this.state.headers
          })
            .then((a) => a.json())
            .then((result) =>
              this.setState({ result: JSON.stringify(result) }, () => {
                this.compareTestWithResponse(result);
              })
            )
            .catch((err) => {
              //console.log("error" + err);
            });
          //console.log(this.state);
        } else {
          fetch(this.state.url, {
            headers: this.state.headers,
            method: this.state.method,
            body: this.state.body
          })
            .then((a) => a.json())
            .then((result) => this.setState({ result: JSON.stringify(result) }))
            .catch((err) => {
              //console.log("error" + err);
            });
          //console.log(this.state);
        }
      });
    } else {
      this.setState({ result: 'EMPTY URL' });
    }
  };

  handleSelectChange = (event) => {
    this.setState({ method: event.target.value });
  };

  handleHeaderRemove = (index) => {
    this.state.headerKeys.splice(index, 1);
    this.state.headerValues.splice(index, 1);
    this.setState({ headerKeys: this.state.headerKeys });
    this.setState({ headervalues: this.state.headerValues });
  };

  handleHeaderKeyChange = (e, index) => {
    this.state.headerKeys[index] = e.target.value;
    // //console.log(this.state.bodyKey);
    this.setState({ headerKeys: this.state.headerKeys });
  };

  handleHeaderValueChange = (e, index) => {
    this.state.headerValues[index] = e.target.value;
    // //console.log(this.state.bodyKey);
    this.setState({ headerValues: this.state.headerValues });
  };

  addHeader = () => {
    this.setState({ headerKeys: [...this.state.headerKeys, ''] });
    this.setState({ headerValues: [...this.state.headerValues, ''] });
  };
  onClickHeader = () => {
    this.setState({ showHeader: true, showBody: false });
  };
  onClickBody = () => {
    this.setState({ showHeader: false, showBody: true });
  };
  handleBodyRawInputChange = (e) => {
    this.setState({ body: e.target.value });
  };
  handleTestChange = (e) => {
    this.setState({ test: e.target.value });
  };

  render() {
    // console.log('these are props',this.props.index)
    // this.props.addCurrentTabId(this.props.index)
    localStorage.setItem('currentTab', this.props.previousTab);
    localStorage.setItem('currentIndex', this.props.index);

    return (
      <div className="urlArea">
        <p
          className="url-title"
          style={{
            fontWeight: 'bold',
            fontFamily: 'Helvetica',
            marginBottom: 0
          }}
        >
          {this.state.url === '' ? 'Untitled' : this.state.url}
        </p>

        <form onSubmit={this.handleFormSubmit}>
          <div className="form-section">
            <select onChange={this.handleSelectChange}>
              {this.state.method === 'GET' ? (
                <option value="GET" selected>
                  GET
                </option>
              ) : (
                <option value="GET">GET</option>
              )}
              {this.state.method === 'POST' ? (
                <option value="POST" selected>
                  POST
                </option>
              ) : (
                <option value="POST">POST</option>
              )}
              {this.state.method === 'PUT' ? (
                <option value="PUT" selected>
                  PUT
                </option>
              ) : (
                <option value="PUT">PUT</option>
              )}
              {this.state.method === 'DELETE' ? (
                <option value="DELETE" selected>
                  DELETE
                </option>
              ) : (
                <option value="DELETE">DELETE</option>
              )}
            </select>
            <input
              type="text"
              placeholder="Enter Request URL"
              value={this.state.url}
              onChange={this.handleUrlChange}
              className="inputUrl"
            />
            <input type="submit" value="Send" className="sendButton" />
          </div>
        </form>

        <Tabs>
          <TabList>
            <Tab>Header</Tab>
            <Tab>Body</Tab>

            <Tab>Test</Tab>
            <Tab>Test Result</Tab>
          </TabList>
          <TabPanel>
            <HttpRequestHeaders
              addHeader={(e) => {
                this.addHeader(e);
              }}
              handleHeaderRemove={(index) => {
                this.handleHeaderRemove(index);
              }}
              handleHeaderValueChange={(e, index) => {
                this.handleHeaderValueChange(e, index);
              }}
              handleHeaderKeyChange={(e, index) => {
                this.handleHeaderKeyChange(e, index);
              }}
              headerValues={this.state.headerValues}
              headerKeys={this.state.headerKeys}
              showHeader={this.state.showHeader}
            />
          </TabPanel>
          <TabPanel>
            <HttpRequestBody
              body={this.state.body}
              showBody={this.state.showBody}
              handleBodyRawInputChange={(e) => {
                this.handleBodyRawInputChange(e);
              }}
            />
          </TabPanel>
          <TabPanel>
            <textarea
              className="test-tab"
              placeholder={'Test'}
              value={this.state.test}
              onChange={(e) => {
                this.handleTestChange(e);
              }}
            />
          </TabPanel>
          <TabPanel>
            <textarea
              className="test-tab"
              placeholder={'Test'}
              value={this.state.testOutput}
            />
          </TabPanel>
        </Tabs>
        <div className="response-label">Response</div>
        <HttpResponse result={this.state.result} />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    historyData: state.historyData,
    tabHistory: state.tabHistory,
    previousTab: state.currentTabId
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    httpHistory: (newHttpData, auth) =>
      dispatch(httpHistory(newHttpData, auth)),
    addCurrentTabId: (currenttab) => dispatch(addCurrentTabId(currenttab))
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HttpRequest);
