import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import React, { Component } from 'react';
import HttpRequest from './HttpRequest';
import uuid from 'uuid';
import { connect } from 'react-redux';

class Newtabs extends Component {
  state = {
    arr: [
      {
        id: 1,
        name: 'GET',
        content: <HttpRequest key={uuid} auth={this.props.auth} />
      }
    ]
  };

  handleClick = () => {
    const { arr } = this.state;
    const newTabObject = {
      id: uuid(),
      name: `GET`,
      content: <HttpRequest key={uuid} auth={this.props.auth} />
    };
    this.setState({
      arr: [...arr, newTabObject]
    });
  };

  handleDelete = (toDelete) => {
    // console.log(toDelete)
    const { arr } = this.state;
    const tabDeleteIndex = arr.findIndex((arr) => arr.id === toDelete);
    const updatedTabs = arr.filter((tab, index) => {
      return index !== tabDeleteIndex;
    });
    // const previousTab=arr[tabDeleteIndex-1] || arr[tabDeleteIndex+1] ||{};
    this.setState({
      arr: updatedTabs
    });
  };
  render() {
    console.log(this.props);
    const { arr } = this.state;
    console.log('arvind dhek array', arr);
    // console.log(this.state.arr)
    const name = arr.map((data, index) => {
      return (
        <Tab>
          <div className="tabListStyle">
          {data.name}{' '}
          <button onClick={() => this.handleDelete(data.id)} className="btn tabListButton">
            X
          </button>
          </div>
        </Tab>
      );
    });
    const content = this.state.arr.map((data) => {
      return (
        <TabPanel>
          <h2>{data.content}</h2>
        </TabPanel>
      );
    });

    const fun = (
      <Tabs>
        <TabList>
          {name}
          <button onClick={this.handleClick} className="btn newTabOpenButton">
            +
          </button>
          
        </TabList>

        {content}
      </Tabs>
    );

    return <div>{fun}</div>;
  }
}

const mapStateToProps = (state) => {
  return {
    auth: state.firebase.auth,
    history: state.firestore.ordered.history
  };
};

export default connect(
  mapStateToProps,
  null
)(Newtabs);
