import React, { Component } from 'react';
import '../App.css';
// import undefined from 'firebase/empty-import';

class Sidebar extends Component {
  state = {
    showHistory: true,
    showCollection: false
  };
  onClickHistory = () => {
    this.setState({
      showHistory: true,
      showCollection: false
    });
  };
  onClickCollection = () => {
    this.setState({
      showCollection: true,
      showHistory: false
    });
  };
  render() {
    console.log('slidebar', this.props.history);
    const history = [];
    if (this.props.history !== undefined) {
      Object.values(this.props.history).map(d => {
        history.push(d)
      })
      console.log(history[0].method)
    }


    return (
      <>
        <div className="historyAndCollection">
          <button onClick={this.onClickHistory}>history</button>
          <button oncClick={this.onClickCollection}>Collection</button>
        </div>
        <div>
          {this.state.showHistory ? <div>{history[0].method}</div> : null}
          {this.state.showCollection ? <div>{}</div> : null}
        </div>
      </>
    );
  }
}

export default Sidebar;
