import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
import React, { Component } from 'react';
import HttpRequest from './HttpRequest';
import uuid from 'uuid';
import { connect } from 'react-redux';
import { addTabHistory } from '../store/actions/tabHistoryActions';

class Newtabs extends Component {
  state = {
    arr: [
      {
        id: uuid(),
        name: 'GET',
        selected: false,
      
      }
    ]
  };
  componentWillUpdate(nextProps, nextState) {
    localStorage.setItem('tabHistory', JSON.stringify(nextState.arr));
  }

  handleClick = () => {
    const { arr } = this.state;
    const newTabObject = {
      id: uuid(),
      name: `GET`,
      selected: false
    };
    this.setState({
      arr: [...arr, newTabObject]
    });
  };
  componentDidMount() {
    const tab = JSON.parse(localStorage.getItem('tabHistory'));
    if(tab!==null)
    this.setState({ arr: tab });

    // }
  }

  handleDelete = (toDelete) => {
    console.log('thing to delete', toDelete);
    const { arr } = this.state;
    const tabDeleteIndex = arr.findIndex((arr) => arr.id === toDelete);
    const updatedTabs = arr.filter((tab, index) => {
      return index !== tabDeleteIndex;
    });
    this.setState({
      arr: updatedTabs
    });
  };
  render() {
    this.props.addTabHistory(this.state);
    const { arr } = this.state;
    const name = arr.map((data, index) => {
      // console.log('this is from new tab',data.id , this.state.arr)
      return (
        <Tab selectedIndex>
          <div className="tabListStyle">
            {data.name}{' '}
            <button
              onClick={() => this.handleDelete(data.id)}
              className="btn tabListButton"
            >
              X
            </button>
          </div>
        </Tab>
      );
    });
    const content = this.state.arr.map((data,index) => {
      return (
        <TabPanel>
          <HttpRequest key={uuid} location={index} index={data.id} auth={this.props.auth} />
          {data.content}
          <HttpRequest
            key={uuid}
            index={data.id}
            auth={this.props.auth}
          />
        </TabPanel>
      );
    });

    const fun = (
      <Tabs>
        <TabList>
          {name}
          <button onClick={this.handleClick} className="btn newTabOpenButton">
            +
          </button>
        </TabList>

        {content}
      </Tabs>
    );

    return <div>{fun}</div>;
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    addTabHistory: (tabData) => dispatch(addTabHistory(tabData))
  };
};

const mapStateToProps = (state) => {
  return {
    auth: state.firebase.auth,
    history: state.firestore.ordered.history
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Newtabs);
