import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { deleteHTTPHistory } from '../store/actions/httpHistoryActions'
import moment from 'moment';


class HistorySummary extends Component {
    state = {
        data: []
    }
    handleDelete = (e) => {
        this.props.deleteHTTPHistory(this.props.historyData);
    }
    handleClick = (e) => {
        console.log('this is target', this.props)
        this.setState({ data: this.props.index })
        console.log('this is state', this.state.data)

    }
    render() {
        return (<React-fragment>
            {this.props.historyData.userId === this.props.auth.uid && <div className='container'  >
                <Link to={'/'}>
                    <div onClick={() => this.handleClick(this)} style={{ color: 'green', textDecoration: 'none', fontWeight: 'bold' }}>{this.props.historyData.method}</div>
                </Link>
                <div>{this.props.historyData.url}
                    <i class="fa fa-trash" aria-hidden="true" onClick={() => this.handleDelete(this)} ></i>
                </div>

                <p style={{ fontWeight: 'bold' }}>{moment(this.props.historyData.createdAt.toDate()).fromNow()}</p>
            </div>}

        </React-fragment>);



    }
}


const mapDispatchToProps = (dispatch) => {
    return {
        deleteHTTPHistory: (history) => dispatch(deleteHTTPHistory(history))
    };

};


export default connect(null, mapDispatchToProps)(HistorySummary)
