    });
    let bodyInput="";
    if(this.state.body.length!==0)
    {
      bodyInput = JSON.parse(this.state.body)
    }

    this.setState({ body: JSON.stringify(bodyInput),headers: headObj },()=>{
      this.props.httpHistory(this.state,this.props.auth)
      console.log(this.state);
      if (this.state.method === "GET") {
        fetch(this.state.url, {
          headers: this.state.headers
        })
          .then(a => a.json())
          .then(result => this.setState({ result: JSON.stringify(result) }))
          .catch(err => {
            //console.log("error" + err);
          });
        //console.log(this.state);
      } else {
        fetch(this.state.url, {
          headers: this.state.headers,
          method: this.state.method,
          body: JSON.stringify(this.state.body)
        })
          .then(a => a.json())
          .then(result => this.setState({ result: JSON.stringify(result) }))
          .catch(err => {
            //console.log("error" + err);
          });
        //console.log(this.state);
      }

    } )
         
        
  };

  handleSelectChange = event => {
    this.setState({ method: event.target.value });
  };

  handleHeaderRemove = index => {
    this.state.headerKeys.splice(index, 1);
    this.state.headerValues.splice(index, 1);
    this.setState({ headerKeys: this.state.headerKeys });
    this.setState({ headervalues: this.state.headerValues });
  };

  handleHeaderKeyChange = (e, index) => {
    this.state.headerKeys[index] = e.target.value;
    // //console.log(this.state.bodyKey);
    this.setState({ headerKeys: this.state.headerKeys });
  };

  handleHeaderValueChange = (e, index) => {
    this.state.headerValues[index] = e.target.value;
    // //console.log(this.state.bodyKey);
    this.setState({ headerValues: this.state.headerValues });
  };

  

  addHeader = () => {
    this.setState({ headerKeys: [...this.state.headerKeys, ""] });
    this.setState({ headerValues: [...this.state.headerValues, ""] });
  };
  onClickHeader = () => {
    this.setState({ showHeader: true, showBody: false });
  };
  onClickBody = () => {
    this.setState({ showHeader: false, showBody: true });
  };
  handleBodyRawInputChange = e => {
    this.setState({ body: e.target.value });
  };

  render() {
    console.log('print history data', this.props.historyData)
    
    return (
      <div className="urlArea">
        {this.state.url==="" ? <p style={{ fontWeight: 'bold', fontFamily: 'Helvetica' }}>Untitled</p> : <p style={{ fontWeight: 'bold', fontFamily: 'Helvetica' }}>{this.state.url}</p>}
          

        <form onSubmit={this.handleFormSubmit}>
          <select onChange={this.handleSelectChange}>
             {this.state.method==="GET"?<option value="GET" selected>GET</option>:<option value="GET">GET</option>}
            {this.state.method==="POST"?<option value="POST" selected>POST</option>:<option value="POST">POST</option>}
            {this.state.method==="PUT"?<option value="PUT" selected>PUT</option>:<option value="PUT">PUT</option>}
            {this.state.method==="DELETE"?<option value="DELETE" selected>DELETE</option>:<option value="DELETE">DELETE</option>}

          </select>
          <input
            type="text"
            value={this.state.url}
            onChange={this.handleUrlChange}
            className="inputUrl"
          />
          <input type="submit" value="Send" className="sendButton" />
        </form>
        
        
        <Tabs>
          <TabList>
            <Tab>
            Header
            </Tab>
            <Tab>
            Body
            </Tab>
          </TabList>
          <TabPanel>
          <HttpRequestHeaders addHeader={(e)=>{this.addHeader(e)}} handleHeaderRemove={(index)=>{this.handleHeaderRemove(index)}} handleHeaderValueChange={(e, index)=>{this.handleHeaderValueChange(e,index)}} handleHeaderKeyChange={(e, index)=>{this.handleHeaderKeyChange(e,index)}} headerValues={this.state.headerValues} headerKeys={this.state.headerKeys} showHeader={this.state.showHeader}/>
        
          </TabPanel>
          <TabPanel>
          <HttpRequestBody body={this.state.body} showBody={this.state.showBody} handleBodyRawInputChange={(e)=>{this.handleBodyRawInputChange(e)}}/>
        
          </TabPanel>
        </Tabs>
       <div className="response-label">
         Response
       </div>
        <HttpResponse result={this.state.result}/>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    historyData: state.historyData
  }
}


const mapDispatchToProps = (dispatch) => {
  return {
    httpHistory: (newHttpData, auth) => dispatch(httpHistory(newHttpData, auth))
  }
}
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HttpRequest);
